import React, {useState} from 'react';

const api =
{
    key: "7c36503de65012532dc787a6f09a76fd",
    base: "https://api.openweathermap.org/data/2.5/"
}

function App() {

  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = evt =>
  {   
    //console.log(evt);
      if(evt.key === "Enter" || evt.type === "click")
      {
        fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
          .then(res => res.json())
          .then(result => {setWeather(result);
          setQuery('');
          //console.log(result);
        });
      }
  }

  const dateBuilder = (d) =>
  {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`
  }
  const timeBuilder = (t) =>
  {
    return t.toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })
  }

  return (

    <div className={
      (typeof weather.main != "undefined") ? ( (Math.round(weather.main.temp) > 25) ? 'app warm' : 'app' )
      : 'app' }>
      <main>
        <div className="search-box">
          <input type="text" className="search-bar" placeholder="Enter..." onChange ={e => setQuery(e.target.value)}
          value= {query}
          onKeyPress={search}/>
          <div className = "button">
          <button type ="submit" className ="btn" onClick={search}>Search</button>
          </div>
        </div>
        {(typeof weather.main != "undefined") ? (
        <div>
        <div className="location-box">
          <div className="location">
            {weather.name},{weather.sys.country}
          </div>
          <div className="date">
           {dateBuilder(new Date())}
           <br></br>
           {timeBuilder(new Date())}
          </div>
          </div>
          <div className="weather-box">
            <div className="temp">
              {Math.round(weather.main.temp)}Â°C
            </div>
            <div className="weather">
            {weather.weather[0].main}
            </div>
            </div>
          </div>
        ) : ('')}
      </main>
    </div>
  );
}


export default App;
